import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { TextField, Button, Typography } from "@mui/material";
import loginBG from "../../images/loginBG5.jpg";
import logoImage from "../../images/CPlogo.png";
import "../../styles/Login.css";

const Login: React.FC = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  const navigate = useNavigate();

  const validate = () => {
    let tempErrors: { [key: string]: string } = {};

    if (!email) {
      tempErrors.email = "This field is required!";
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      tempErrors.email = "This is not a valid email.";
    }

    if (!password) {
      tempErrors.password = "This field is required!";
    }

    setErrors(tempErrors);

    return Object.keys(tempErrors).length === 0;
  };

  const handleLogin = () => {
    if (!validate()) {
      return;
    }

    navigate("/dashboard");
  };

  const handleInputChange = (setter: React.Dispatch<React.SetStateAction<string>>, field: string) => (e: React.ChangeEvent<HTMLInputElement>) => {
    setter(e.target.value);
    setErrors((prevErrors) => {
      const { [field]: removedError, ...rest } = prevErrors;
      return rest;
    });
  };

  return (
    <div className="root-container" style={{ backgroundImage: `url(${loginBG})` }}>
      <div className="login-box">
        <img
          src={logoImage}
          alt="Crashpad Logo"
          className="logo"
        />
        <form noValidate autoComplete="off">
          <TextField
            fullWidth
            margin="normal"
            id="email"
            label="Email Address"
            variant="outlined"
            value={email}
            onChange={handleInputChange(setEmail, "email")}
            error={!!errors.email}
            helperText={errors.email}
          />
          <TextField
            fullWidth
            margin="normal"
            id="password"
            label="Password"
            type="password"
            variant="outlined"
            value={password}
            onChange={handleInputChange(setPassword, "password")}
            error={!!errors.password}
            helperText={errors.password}
          />
          <Button
            fullWidth
            variant="contained"
            className="login-button"
            onClick={handleLogin}
          >
            Login
          </Button>
          <Typography variant="body2" style={{ marginTop: "1em" }}>
            New to Crashpad?
            <Link to="/SignUp" className="signup-link">
              Sign up here
            </Link>
          </Typography>
        </form>
      </div>
    </div>
  );
};

export default Login;
