import axios from "axios";

const API_URL = "http://localhost:8080/";

class PasswordReset {

  verifyEmail(email: string)  {
    return axios.post(API_URL + `verifyMail/${email}`);
  };

  verifyOtp(otp: string, email: string) {
    return axios.post(API_URL + `forgotPassword/verifyOtp/${otp}/${email}`);
  };

  changePassword(password: string, repeatPassword: string, email: string) {
    return axios.post(API_URL + `forgotPassword/changePassword/${email}`, {
      password,
      repeatPassword
    });
  };

  forgotPassword(forgotUserName: string) {
    return axios.post(API_URL + "forgotpassword", {
      username: forgotUserName
    });
  }

}

export default new PasswordReset();
