import React, { useState } from "react";
import { TextField, Container, Typography, Button, Link } from "@mui/material";
import { useNavigate } from "react-router-dom";
//import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import logoImage from "../../images/registration/CPlogo.png";
import loginBG from "../../images/registration/loginBG5.jpg";
import "../../styles/Login.css";
import AuthService from "../../services/registration/authentication";

const Login: React.FC = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  const navigate = useNavigate();

  const validate = () => {
    let tempErrors: { [key: string]: string } = {};


    if (!username) {
      tempErrors.username = "This field is required!";
    } else if (username.length < 4 || username.length > 20) {
      tempErrors.username = "The username must be between 4 and 20 characters.";
    }

    if (!password) {
      tempErrors.password = "This field is required!";
    } else if (password.length < 6 || password.length > 20) {
      tempErrors.password = "The password must be between 6 and 20 characters.";
    }

    setErrors(tempErrors);

    return Object.keys(tempErrors).length === 0;
  };

  const handleLogin = () => {
    if (!validate()) {
      return;
    }

    setMessage("");

    AuthService.login(email, password).then(
      (response) => {
        navigate("/dashboard");
      },
      (error) => {
        const resMessage =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        setMessage(resMessage);
      }
    );
  };

  const handleGoogleLoginSuccess = (response: any) => {
    console.log(response);
    //ToDo : maybe send the response token to  server for verification and further processing
    navigate("/dashboard");
  };

  const handleGoogleLoginFailure = (response: any) => {
    console.error(response);
  };

  return (
      <div className="root-container" style={{ backgroundImage: `url(${loginBG})` }}>
        <Container className="login-container">
          <div className="login-box">
            <img
              src={logoImage}
              alt="Crashpad Logo"
              style={{ width: "50%", height: "auto", marginLeft: "6em" }}
            />
            <form noValidate autoComplete="off">
              <TextField
                fullWidth
                margin="normal"
                id="email"
                label="Email Address"
                variant="outlined"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                error={!!errors.email}
                helperText={errors.email}
              />
              <TextField
                fullWidth
                margin="normal"
                id="password"
                label="Password"
                type="password"
                variant="outlined"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                error={!!errors.password}
                helperText={errors.password}
              />
              <Button
                className="login-button"
                fullWidth
                variant="contained"
                onClick={handleLogin}
              >
                Login
              </Button>

              {message && (
                <div className="form-group">
                  <div className="alert alert-danger" role="alert">
                    {message}
                  </div>
                </div>
              )}
              <Typography
                variant="body2"
                style={{ marginTop: "1em", textAlign: "center" }}
              >
                New user?{" "}
                <Link href="/signup" underline="none">
                  Sign Up
                </Link>
              </Typography>
            </form>
          </div>
        </Container>
      </div>
  );
};

export default Login;
