import React, { useState } from "react";
import { TextField, Container, Typography, Button, Link } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import logoImage from "../../images/registration/CPlogo.png";
import loginBG from "../../images/registration/loginBG5.jpg";
import "../../styles/Login.css";
import AuthService from "../../services/registration/authentication";

const Login: React.FC = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  const navigate = useNavigate();

  const validate = () => {
    let tempErrors: { [key: string]: string } = {};

    if (!email) {
      tempErrors.email = "This field is required!";
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      tempErrors.email = "This is not a valid email.";
    }

    if (!password) {
      tempErrors.password = "This field is required!";
    }

    setErrors(tempErrors);

    return Object.keys(tempErrors).length === 0;
  };



  const handleLogin = () => {
    if (!validate()) {
      return;
    }

    setMessage("");

    AuthService.login(email, password).then(
      (response) => {
        navigate("/dashboard");
      },
      (error) => {
        const resMessage =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        setMessage(resMessage);
      }
    );
  };

  const handleGoogleLoginSuccess = (response: any) => {
    console.log(response);
    // You can send the response token to your server for verification and further processing
    navigate("/dashboard");
  };

  const handleGoogleLoginFailure = (response: any) => {
    console.error(response);
  };

  return (
    <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
      <div className="root-container" style={{ backgroundImage: `url(${loginBG})` }}>
        <Container className="login-container">
          <div className="login-box">
            <img
              src={logoImage}
              alt="Crashpad Logo"
              style={{ width: "50%", height: "auto", marginLeft: "6em" }}
            />
            <form noValidate autoComplete="off">
              <TextField
                fullWidth
                margin="normal"
                id="email"
                label="Email Address"
                variant="outlined"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                error={!!errors.email}
                helperText={errors.email}
              />
              <TextField
                fullWidth
                margin="normal"
                id="password"
                label="Password"
                type="password"
                variant="outlined"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                error={!!errors.password}
                helperText={errors.password}
              />
              <Button
                className="login-button"
                fullWidth
                variant="contained"
                onClick={handleLogin}
              >
                Login
              </Button>
              <div style={{ textAlign: "center", margin: "1em 0" }}>
              <GoogleLogin
                onSuccess={handleGoogleLoginSuccess}
              />
              </div>
              {message && (
                <div className="form-group">
                  <div className="alert alert-danger" role="alert">
                    {message}
                  </div>
                </div>
              )}
              <Typography
                variant="body2"
                style={{ marginTop: "1em", textAlign: "center" }}
              >
                New user?{" "}
                <Link href="/signup" underline="none">
                  Sign Up
                </Link>
              </Typography>
            </form>
          </div>
        </Container>
      </div>
    </GoogleOAuthProvider>
  );
};

export default Login;
