import React, { useState } from "react";
import { Modal, Box, Typography, TextField, Button } from "@mui/material";
import PasswordReset from "../../services/registration/passwordReset";

interface ForgotPasswordProps {
  open: boolean;
  onClose: () => void;
}

const ForgotPassword: React.FC<ForgotPasswordProps> = ({ open, onClose }) => {
  const [forgotUserName, setForgotUserName] = useState("");
  const [otp, setOtp] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [repeatPassword, setRepeatPassword] = useState("");
  const [forgotMessage, setForgotMessage] = useState("");
  const [step, setStep] = useState(1);

  const handleForgotPasswordSubmit = () => {
    PasswordReset.verifyEmail(forgotUserName).then(
      (response) => {
        setForgotMessage("A reset link has been sent to your email.");
        setStep(2);
      },
      (error) => {
        const resMessage =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();
        setForgotMessage(resMessage);
      }
    );
  };

  const handleVerifyOtp = () => {
    PasswordReset.verifyOtp(otp, forgotUserName).then(
      (response) => {
        setForgotMessage("OTP verified.");
        setStep(3);
      },
      (error) => {
        const resMessage =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();
        setForgotMessage(resMessage);
      }
    );
  };

  const handleChangePassword = () => {
    if (newPassword !== repeatPassword) {
      setForgotMessage("Passwords do not match!");
      return;
    }

    PasswordReset.changePassword(newPassword, repeatPassword, forgotUserName).then(
      (response) => {
        setForgotMessage("Password has been changed!");
        onClose();
      },
      (error) => {
        const resMessage =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();
        setForgotMessage(resMessage);
      }
    );
  };

  return (
    <Modal open={open} onClose={onClose}>
      <Box className="modal-box" style={{ width: 400, margin: 'auto', marginTop: '10%', padding: '20px', backgroundColor: 'white', borderRadius: '8px' }}>
        <Typography variant="h5" className="modal-title">
          Forgot Password
        </Typography>
        {step === 1 && (
          <>
            <TextField
              fullWidth
              margin="normal"
              id="forgot-username"
              label="Email Address"
              variant="outlined"
              value={forgotUserName}
              onChange={(e) => setForgotUserName(e.target.value)}
            />
            <Button
              fullWidth
              variant="contained"
              onClick={handleForgotPasswordSubmit}
              className="modal-button"
            >
              Send OTP
            </Button>
          </>
        )}
        {step === 2 && (
          <>
            <TextField
              fullWidth
              margin="normal"
              id="otp"
              label="OTP"
              variant="outlined"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
            />
            <Button
              fullWidth
              variant="contained"
              onClick={handleVerifyOtp}
              className="modal-button"
            >
              Verify OTP
            </Button>
          </>
        )}
        {step === 3 && (
          <>
            <TextField
              fullWidth
              margin="normal"
              id="new-password"
              label="New Password"
              type="password"
              variant="outlined"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
            <TextField
              fullWidth
              margin="normal"
              id="repeat-password"
              label="Repeat Password"
              type="password"
              variant="outlined"
              value={repeatPassword}
              onChange={(e) => setRepeatPassword(e.target.value)}
            />
            <Button
              fullWidth
              variant="contained"
              onClick={handleChangePassword}
              className="modal-button"
            >
              Change Password
            </Button>
          </>
        )}
        {forgotMessage && (
          <div className="form-group">
            <div
              className={forgotMessage.includes("has been sent") || forgotMessage.includes("OTP verified") ? "alert alert-success" : "alert alert-danger"}
              role="alert"
            >
              {forgotMessage}
            </div>
          </div>
        )}
      </Box>
    </Modal>
  );
};

export default ForgotPassword;
