// src/pages/SignUp.tsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import AccountForm from "./AccountForm";
import AuthService from "../../services/registration/authentication";
import loginBG from "../../images/registration/loginBG5.jpg";
import logoImage from "../../images/registration/CPlogo.png";
import "../../styles/Signup.css";

const SignUp: React.FC = () => {
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleRegister = (username: string, email: string, password: string, isTraveller: boolean, isHost: boolean) => {
    AuthService.register(username, email, password).then(
      (response) => {
        setMessage(response.data.message);
        navigate("/dashboard");
      },
      (error) => {
        const resMessage =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        setMessage(resMessage);
      }
    );
  };

  return (
    <div className="root-container" style={{ backgroundImage: `url(${loginBG})` }}>
      <div className="signup-box">
        <img
          src={logoImage}
          alt="Crashpad Logo"
          style={{ width: "50%", height: "auto", marginLeft: "6em" }}
        />
        <AccountForm
          isLogin={false}
          onSubmit={handleRegister}
          message={message}
          setMessage={setMessage}
        />
      </div>
    </div>
  );
};

export default SignUp;
