// src/pages/Login.tsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import AccountForm from "./AccountForm";
import AuthService from "../../services/registration/authentication";
import loginBG from "../../images/registration/loginBG5.jpg";
import logoImage from "../../images/registration/CPlogo.png";
import "../../styles/Login.css";

const Login: React.FC = () => {
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = (username: string, email: string, password: string) => {
    AuthService.login(username, password).then(
      (response) => {
        navigate("/dashboard");
      },
      (error) => {
        const resMessage =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        if (error.response && error.response.status === 401) {
          setMessage("Invalid username or password. Please try again.");
        } else {
          setMessage(resMessage);
        }
      }
    );
  };

  return (
    <div className="root-container" style={{ backgroundImage: `url(${loginBG})` }}>
      <div className="login-box">
        <img
          src={logoImage}
          alt="Crashpad Logo"
          style={{ width: "50%", height: "auto", marginLeft: "6em" }}
        />
        <AccountForm
          isLogin={true}
          onSubmit={handleLogin}
          message={message}
          setMessage={setMessage}
        />
      </div>
    </div>
  );
};

export default Login;
